import React from "react";

function Form({ query, changeInput, submitSearch }) {
  return (
    <form>
      <div className="form-group">
        <label htmlFor="queryuery">
          <strong>Book</strong>
        </label>
        <input
          className="form-control"
          id="Title"
          type="text"
          value={query}
          placeholder="Search by title or author"
          name="query"
          onChange={changeInput}
          requeryuired
        />
      </div>
      <div className="pull-right">
        <button
          onClick={submitSearch}
          type="submit"
          className="btn btn-lg btn-info float-left"
        >
          Search
        </button>
      </div>
    </form>
  );
}

export default Form;
